mapper.select:
SELECT order_id, product_name, pay_url, pay_status, total_amount
FROM openai_order
WHERE openid = #{openid}
  AND product_id = #{productId}
  AND order_status = 0
ORDER BY id desc LIMIT 1

mapper.update:
UPDATE openai_order
SET pay_url     = #{payUrl},
    pay_status  = #{payStatus},
    update_time = now()
WHERE openid = #{openid}
  AND order_id = #{orderId}
